function slug(title: string) {
  return title.toLowerCase().replace(/[^\w\s-]/g, '').replace(/\s+/g, '-').replace(/-+/g, '-').trim().slice(0, 80) + '-' + Date.now().toString(36) + Math.random().toString(36).slice(2, 5);
}

