async function main() {
  if (!MONGO_URI) {
    console.error('MONGODB_URI not set');
    process.exit(1);
  }

  await mongoose.connect(MONGO_URI);
  console.log('Connected to MongoDB');

  // Ensure a seed user exists
  const UserModel = mongoose.models.User || mongoose.model('User', new mongoose.Schema({
    name: String, username: String, email: String, password: String,
    provider: String, role: String, promptCount: Number, likeCount: Number,
  }, { timestamps: true }));

  let seedUser = await UserModel.findOne({ username: SEED_USER.username });
  if (!seedUser) {
    seedUser = await UserModel.create({
      _id: SEED_USER._id,
      name: SEED_USER.name,
      username: SEED_USER.username,
      email: 'team@promptall.net',
      provider: 'email',
      role: 'admin',
      promptCount: 0,
      likeCount: 0,
    });
    console.log('Created seed user');
  }

  const PromptModel = mongoose.models.Prompt || mongoose.model('Prompt', new mongoose.Schema({
    title: String, content: String, description: String, aiTool: String, category: String,
    tags: [String], resultText: String, resultImages: [String], resultLink: String,
    author: mongoose.Schema.Types.ObjectId, authorName: String, authorUsername: String,
    likeCount: Number, commentCount: Number, bookmarkCount: Number,
    viewCount: Number, copyCount: Number, trendingScore: Number,
    status: String, reportCount: Number, slug: String, language: String,
  }, { timestamps: true }));

  let inserted = 0;
  for (const p of PROMPTS) {
    const s = slug(p.title);
    try {
      await PromptModel.create({
        title: p.title,
        content: p.content,
        aiTool: p.aiTool,
        category: p.category,
        tags: p.tags || [],
        resultImages: [],
        author: seedUser._id,
        authorName: seedUser.name,
        authorUsername: seedUser.username,
        likeCount: p.likeCount || 0,
        commentCount: 0,
        bookmarkCount: 0,
        viewCount: Math.floor(Math.random() * 5000) + 500,
        copyCount: Math.floor(Math.random() * 1000) + 100,
        trendingScore: p.trendingScore || 50,
        status: 'active',
        reportCount: 0,
        slug: s,
        language: 'en',
      });
      inserted++;
      process.stdout.write(`\rInserted ${inserted}/${PROMPTS.length}`);
    } catch (e: any) {
      console.error(`\nFailed: ${p.title} - ${e.message}`);
    }
  }

  await UserModel.findByIdAndUpdate(seedUser._id, { promptCount: inserted });
  console.log(`\nDone! Inserted ${inserted} prompts.`);
  await mongoose.disconnect();
}

main().catch(console.error);
